import React, { useContext, useState } from 'react';
import axios from 'axios';
import { AuthContext } from './AuthContext';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { useHistory, Link } from 'react-router-dom';

const LoginForm = () => {
  const { login, refreshToken } = useContext(AuthContext);
  const [data, setLoginData] = useState({ admin_email: '', password: '' });
  const history = useHistory();

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setLoginData(prev => ({ ...prev, [name]: value }));
  };

  // Validate inputs
  const validateInputs = () => {
    if (!data.admin_email || !data.admin_email.includes('@')) {
      toast.error('Enter a valid email', { position: toast.POSITION.TOP_RIGHT, autoClose: 3000 });
      return false;
    }
    if (data.password.length < 8) {
      toast.error('Password must be at least 8 characters', { position: toast.POSITION.TOP_RIGHT, autoClose: 3000 });
      return false;
    }
    return true;
  };

  // Handle login
  const handleLogin = async (e) => {
    e.preventDefault();

    if (!validateInputs()) return; // Stop if inputs are invalid

    try {
      const response = await axios.post('http://localhost:4000/api/admin/loginAdmin', data);
      
      if (response.status === 200) {
        const { accessToken, refreshToken: newRefreshToken } = response.data;

        // Store tokens and navigate to dashboard
        sessionStorage.setItem('accessToken', accessToken);
        sessionStorage.setItem('refreshToken', newRefreshToken);
        login(accessToken);
        history.replace('/dashboard');

        toast.success(`Login Successful. Welcome, ${data.admin_email}`, { position: toast.POSITION.TOP_RIGHT, autoClose: 3000 });
      } else if (response.status === 401) {
        const newAccessToken = await refreshToken();
        if (newAccessToken) await handleLogin(e);
        else toast.error('Invalid username/password', { position: toast.POSITION.TOP_RIGHT, autoClose: 3000 });
      } else {
        toast.error('Authentication Failed', { position: toast.POSITION.TOP_RIGHT, autoClose: 3000 });
      }
    } catch (error) {
      console.error('Error:', error);
      toast.error('Invalid username/password', { position: toast.POSITION.TOP_RIGHT, autoClose: 3000 });
    }
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen">
      <form onSubmit={handleLogin} className="flex flex-col bg-white p-10 rounded-lg shadow-md gap-5">
        <h1 className="text-2xl text-center font-bold mb-4">Hello Admin</h1>
        <h2 className="text-xl text-center mb-4">Sign In to Game Box</h2>
        <label className="block">Email</label>
        <input
          type="text"
          placeholder="Enter your Email"
          name="admin_email"
          value={data.admin_email}
          onChange={handleChange}
          className="input-field mb-4 px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:border-blue-500"
        />
        <label className="block">Password</label>
        <input
          type="password"
          placeholder="Enter Your Password"
          name="password"
          value={data.password}
          onChange={handleChange}
          className="input-field mb-4 px-4 py-2 rounded-lg border border-gray-300 focus:outline-none focus:border-blue-500"
        />
        <button
          type="submit"
          className="btn bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline mb-5"
        >
          Login
        </button>
        <Link to="/loginUser" className="text-blue-500 hover:underline">
          Login as User
        </Link>
        <ToastContainer />
      </form>
    </div>
  );
};

export default LoginForm;
